(()=>{"use strict";document.body.addEventListener("scroll",(e=>{const c=document.querySelector(".nav");document.body.scrollTop>25?c.classList.add("nav_scrolled"):c.classList.remove("nav_scrolled")}));const e=document.querySelector(".nav__burger");e&&e.addEventListener("click",(()=>{if(window.innerWidth<=800){const e=document.querySelector(".nav");e.classList.contains("nav_active")?e.classList.remove("nav_active"):e.classList.add("nav_active")}}))})();